dt-example.js:
$(document).ready(function() {
	$('#example').DataTable( {
		autoFill: {
			enable: false
		},
		dom: 'Bfrtip',
		buttons: [
			{
				text: "Enable AutoFill",
				action: function (e, dt) {
					if ( dt.autoFill().enabled() ) {
						this.autoFill().disable();
						this.text( 'Enable AutoFill' );
					}
					else {
						this.autoFill().enable();
						this.text( 'Disable AutoFill' );
					}
				}
			}
		]
	} );
} );
